using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace SimplePDF
{
    class Program
    {
        static void Main(string[] args)
        {
            PDFDocument pdfDocument = new PDFDocument(".", "test");
            Font normal = pdfDocument.NewFont("Normal", "Helvetica");
            Base64JPGStream base64JPGStream = pdfDocument.NewBase64JPG(
                "nerdfacejpg"
                , @""
                , (20, 20)
                , (20, 700)
            );
            StringBuilder stream = pdfDocument.NewPage(new List<DictionaryObject> { normal, base64JPGStream });
            stream.Append($"\nBT /Normal 12 Tf 190 650 Td (check out the pdf docs first) Tj ET\n");
            stream.Append($"BT /Normal 12 Tf 190 630 Td (you'd be fine with just learning: BT, ET, Tf, Td, and Tj) Tj ET\n");
            pdfDocument.Create();
        }
    }
}
